package Course01Mod2Dna;


/**
 * Java class that finds and returns all genes from a given dna string
 * 
 * @author (Ammar) 
 * @version (a version number or a date)
 */
public class Dna {
    //this method is created so that we can get index of all three stop codons
    //of TAA, TAG, TGA
    public int findStopCodon(String dnaString, int startIndex, String stopCodon){
        //current stop index, which'll be the stopCodon searching from startIndex cuz obv
        int currentIndex = dnaString.indexOf(stopCodon, startIndex+3);
        
        //we loop through till currentIndex is -1
        while(currentIndex != -1) {
            //if the gap between our end and start is a multiple of 3, we are done
            if((currentIndex - startIndex) % 3 == 0) {
                return currentIndex;
            } else {    //else we shift the currentIndex forward to start from the previous stop
                currentIndex = dnaString.indexOf(stopCodon, currentIndex+1);
            }
        }
        //if theres no valid index we return -1, cuz its noiceeeee
        return -1;
    }
    
    public String findGene(String dna, int nextDna) {
        //if start not found simply return blank string
        int startIndex = dna.indexOf("ATG", nextDna);
        if(startIndex==-1) return "";
        
        //we search all the indexes of the three different stop codons
        int taaIndex = findStopCodon(dna, startIndex, "TAA");
        int tagIndex = findStopCodon(dna, startIndex, "TAG");
        int tgaIndex = findStopCodon(dna, startIndex, "TGA");
        
        //we need the minimum ofc to get the first ending of the dna
        int minIndex = 0;
        
        //here what we do is get the minimum index out of the three indices, excluding -1
        if(taaIndex == -1 || (tgaIndex != -1 && tgaIndex<taaIndex)) {
            minIndex = tgaIndex;
        } else {
            minIndex = taaIndex;
        }
        
        if(minIndex == -1 || (tagIndex != -1 && tagIndex < minIndex)) {
            minIndex = tagIndex;
        }
        
        if(minIndex ==-1) return "";
        
        //if everything is valid finally we return the actual dna
        return dna.substring(startIndex, minIndex + 3);
    }
    
    //this method will print all dna within a strand of dna, all the above methods does it for a single dna
    public void printAllGenes(String dna) {
        int startIndex = 0;
        //int mainCount = 0;
        //loop thru entire gene and call methods to find gene
        while(true) {
            String currentGene = findGene(dna, startIndex);
            //if found gene isnt present break out of the loop
            if(currentGene.isEmpty()) break;
            else {
                //double count = 0;
                //for(int i=0;i<currentGene.length();i++) {
                //    if(currentGene.charAt(i) == 'G' || currentGene.charAt(i) == 'C') count++;
                //}
                //double ratio = count/currentGene.length();
                //if(ratio > 0.35) {
                //    mainCount++;
                //}
                System.out.println(currentGene);    //else print the gene
                //and move the start index to after the current refered dna
                startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
            }            
        }
        //System.out.println(mainCount);
    }
    
    public void testOn(String dna) {
        System.out.println("Testing Genes " + dna);
        printAllGenes(dna);
    }
    
    public void Test() {
        testOn("");
        testOn("");;
        testOn("ATGATCTAA");
        
    }
}
